mapper.select:
select * from crew_like
where member_id = #{memberNo}
and crew_no = #{crewNo}

mapper.insert:
insert into crew_like (
    member_id, crew_no
) values (
    #{memberNo}, #{crewNo}
)

mapper.delete:
delete from crew_like
where member_id = #{memberNo}
and crew_no = #{crewNo}

mapper.select:
select * from crew 
where crew_category = #{category}
and crew_location = #{location}

    and instr(category_name, #{keyword}) > 0

select * from crew

where crew_no in



    #{crewNo}
