mapper.delete:
delete from crew_member
where crew_no = #{crewNo}
and member_no = #{memberNo}

mapper.select:
select
case when leader = 'Y' then 1 else 0 end as isLeader
from crew_member
where crew_no = #{crewNo}
and member_no = #{memberNo}

		select 
        	case when count(*) > 0 then 1 else 0 end
    	from crew_member
		where crew_no = #{crewNo}
		and member_no = #{memberNo}

