mapper.insert:
insert into reply (
    reply_no, reply_writer, reply_origin, reply_content, reply_wtime, reply_utime
) values (
    reply_seq.nextval, #{replyWriter}, #{replyOrigin}, #{replyContent}, systimestamp, null
)

mapper.update:
update board
set board_reply = board_reply + 1
where board_no = #{replyOrigin}

update board
set board_reply = board_reply - 1
where board_no = #{replyOrigin}
